import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 23.1-1901 Membership; executive committee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-1901.md","filePath":"vacode/23.1-1901.md"}'),o={name:"vacode/23.1-1901.md"},i=r('<h1 id="§-23-1-1901-membership-executive-committee" tabindex="-1">§ 23.1-1901 Membership; executive committee. <a class="header-anchor" href="#§-23-1-1901-membership-executive-committee" aria-label="Permalink to &quot;§ 23.1-1901 Membership; executive committee.&quot;">​</a></h1><p>A. The board of visitors shall consist of 13 members appointed by the Governor, of whom at least four shall be alumni of the University. Of the alumni appointed, at least one shall be a resident of the Commonwealth.</p><p>B. The alumni association of the University may submit to the Governor a list of four nominees for each vacancy on the board, whether the vacancy occurs by expiration of a term or otherwise. The Governor may appoint a member from the list of nominees.</p><p>C. The board may appoint at least three and not more than five of its members to an executive committee that has and may exercise such powers as the board may prescribe.</p><p>1968, c. 532, § 23-174.4; 1980, c. 625; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP1013" target="_blank" rel="noreferrer">1013</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0802" target="_blank" rel="noreferrer">802</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),n=[i];function s(c,h,m,l,p,f){return t(),a("div",null,n)}const d=e(o,[["render",s]]);export{b as __pageData,d as default};
