import{_ as e,o as t,c as r,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 23.1-207 Tuition relief and refunds and reinstatement for certain students in the Armed Forces.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-207.md","filePath":"vacode/23.1-207.md"}'),i={name:"vacode/23.1-207.md"},o=n('<h1 id="§-23-1-207-tuition-relief-and-refunds-and-reinstatement-for-certain-students-in-the-armed-forces" tabindex="-1">§ 23.1-207 Tuition relief and refunds and reinstatement for certain students in the Armed Forces. <a class="header-anchor" href="#§-23-1-207-tuition-relief-and-refunds-and-reinstatement-for-certain-students-in-the-armed-forces" aria-label="Permalink to &quot;§ 23.1-207 Tuition relief and refunds and reinstatement for certain students in the Armed Forces.&quot;">​</a></h1><p>A. The Council shall issue and revise guidelines for tuition relief, refunds, and reinstatement for students whose service in the Armed Forces of the United States or the Commonwealth has required their sudden withdrawal or prolonged absence from their enrollment in a public institution of higher education and shall provide for the required reenrollment of such students by the relevant institution. These guidelines shall be excluded from the provisions of the Administrative Process Act pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-4002/" target="_blank" rel="noreferrer">2.2-4002</a>.</p><p>B. The Council shall appoint an advisory committee of at least 10 representatives of the public institutions of higher education to assist in the development and subsequent revision of such guidelines. The Council shall consult with the Office of the Attorney General and provide opportunity for public comment prior to issuing such guidelines or revisions.</p><p>C. Such guidelines shall include procedures for the required reenrollment of students whose service in the Armed Forces of the United States or the Commonwealth precluded their completion of a semester or equivalent term and policies for the required reenrollment of such students in such armed forces.</p><p>1991, c. 80, § 23-9.6:2; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0027" target="_blank" rel="noreferrer">27</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),s=[o];function a(d,l,c,u,h,f){return t(),r("div",null,s)}const _=e(i,[["render",a]]);export{m as __pageData,_ as default};
