import{_ as e,o as t,c as r,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 23.1-230 Enrollment agreements required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-230.md","filePath":"vacode/23.1-230.md"}'),a={name:"vacode/23.1-230.md"},o=n('<h1 id="§-23-1-230-enrollment-agreements-required" tabindex="-1">§ 23.1-230 Enrollment agreements required. <a class="header-anchor" href="#§-23-1-230-enrollment-agreements-required" aria-label="Permalink to &quot;§ 23.1-230 Enrollment agreements required.&quot;">​</a></h1><p>A. No postsecondary school that is required to be certified by the Council shall enroll students without entering into an enrollment agreement with each student. Such enrollment agreement shall be signed by the student and an authorized representative of the school and shall contain all disclosures prescribed by the Council.</p><p>B. No postsecondary school that is required to be certified by the Council shall condition the enrollment of a student on:</p><ol><li><p>Entering into an agreement that requires the student to arbitrate any dispute between the student and the school, regardless of whether the agreement permits the student to opt out of the requirement to arbitrate any such dispute in the future; or</p></li><li><p>Entering into an agreement that requires the student to resolve a dispute on an individual basis and waive the right to class or group actions.</p></li></ol><p>2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0298" target="_blank" rel="noreferrer">298</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1135" target="_blank" rel="noreferrer">1135</a>.</p>',5),i=[o];function s(l,d,h,c,u,p){return t(),r("div",null,i)}const g=e(a,[["render",s]]);export{m as __pageData,g as default};
