import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 23.1-2304 Principal office; meetings; officers; committees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2304.md","filePath":"vacode/23.1-2304.md"}'),o={name:"vacode/23.1-2304.md"},r=i('<h1 id="§-23-1-2304-principal-office-meetings-officers-committees" tabindex="-1">§ 23.1-2304 Principal office; meetings; officers; committees. <a class="header-anchor" href="#§-23-1-2304-principal-office-meetings-officers-committees" aria-label="Permalink to &quot;§ 23.1-2304 Principal office; meetings; officers; committees.&quot;">​</a></h1><p>A. The principal office of the board shall be located, and all meetings of the board held, as far as practicable, in the City of Richmond.</p><p>B. The board shall meet at least once a year and at such other times as it determines. Notice of all meetings shall be provided to each member.</p><p>C. A majority of the members shall constitute a quorum.</p><p>D. The board shall appoint from its membership a rector, a vice-rector, a secretary, and any other officers as determined by the board. The board shall prescribe their duties and term of office and fix their compensation, if any.</p><p>E. The board shall determine the number of members of and appoint an executive committee and determine the number of members of the executive committee that shall constitute a quorum. The executive committee shall perform duties prescribed by the board.</p><p>F. Reasonable expenses incurred by members shall be paid out of the funds of the University.</p><p>1968, c. 93, § 23-50.9; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',8),s=[r];function c(n,m,f,l,d,h){return t(),a("div",null,s)}const _=e(o,[["render",c]]);export{b as __pageData,_ as default};
