import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 23.1-2608 Definitions; Virginia Cooperative Extension Service and Agricultural Experiment Station Division established; Cooperative Extension Service Program recognized.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2608.md","filePath":"vacode/23.1-2608.md"}'),n={name:"vacode/23.1-2608.md"},o=r('<h1 id="§-23-1-2608-definitions-virginia-cooperative-extension-service-and-agricultural-experiment-station-division-established-cooperative-extension-service-program-recognized" tabindex="-1">§ 23.1-2608 Definitions; Virginia Cooperative Extension Service and Agricultural Experiment Station Division established; Cooperative Extension Service Program recognized. <a class="header-anchor" href="#§-23-1-2608-definitions-virginia-cooperative-extension-service-and-agricultural-experiment-station-division-established-cooperative-extension-service-program-recognized" aria-label="Permalink to &quot;§ 23.1-2608 Definitions; Virginia Cooperative Extension Service and Agricultural Experiment Station Division established; Cooperative Extension Service Program recognized.&quot;">​</a></h1><p>A. For the purposes of this article:</p><p>&quot;Cooperative extension service&quot; means the function traditionally associated with the term &quot;extension&quot; that traditionally focuses on agriculture, including horticulture and silviculture, agribusiness, home economics, community resource development, and 4-H Clubs.</p><p>&quot;Extension&quot; means the joint federal, state, and local program designed to aid the transfer of information and research capabilities of land-grant universities to citizens.</p><p>B. There is established within the University a division to be known as the Virginia Cooperative Extension Service and Agricultural Experiment Station Division (the Division), which shall encompass and administer the Virginia Cooperative Extension Service (the Service) and the Agricultural Experiment Station (the Station) with appropriate supporting programs.</p><p>C. The Cooperative Extension Service Program within Virginia State University (the Program) is recognized. The Program shall be operated cooperatively by the University and Virginia State University, with agreed-upon areas of program and service emphasis as set forth in the unified plan submitted by the two institutions to the U.S. Department of Agriculture.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0433" target="_blank" rel="noreferrer">433</a>, §§ 23-132.1, 23-132.7; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',7),a=[o];function s(c,p,l,d,h,v){return i(),t("div",null,a)}const m=e(n,[["render",s]]);export{u as __pageData,m as default};
