import{_ as t,o,c as i,z as e,a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 23.1-2616 Station; agricultural survey.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2616.md","filePath":"vacode/23.1-2616.md"}'),n={name:"vacode/23.1-2616.md"},r=e("h1",{id:"§-23-1-2616-station-agricultural-survey",tabindex:"-1"},[a("§ 23.1-2616 Station; agricultural survey. "),e("a",{class:"header-anchor",href:"#§-23-1-2616-station-agricultural-survey","aria-label":'Permalink to "§ 23.1-2616 Station; agricultural survey."'},"​")],-1),s=e("p",null,"The Station may direct and supervise a thorough and comprehensive agricultural survey of the Commonwealth according to the most approved methods in practice to gather facts and information on existing agricultural conditions in the Commonwealth and data upon which to base a study of agricultural economics and a constructive program for the development of agriculture and agricultural resources. The survey shall examine (i) soils and soil fertility and management; (ii) soil erosion and drainage problems affecting soil fertility and productivity; (iii) the adaptation of various soil types, elevations, and seasonable conditions to crops produced or that may suitably be produced; (iv) farm layout and selection; (v) arrangement of fields for the use of labor-saving machinery; (vi) economy and convenience in cultivation and farm operations; (vii) methods of cultivation, production, and handling of crops; (viii) general farm management; (ix) the various crops produced on farms and their yield and gross value compared with the cost of production and courses of low yield; (x) farm labor and its distribution and efficiency; (xi) labor incomes of the various classes of farm labor; (xii) the relation of various farm products to public needs and local and general supply and demand; (xiii) farm incomes and income sources; (xiv) capital investment and return; (xv) distribution of capital investment; (xvi) the character and extent of idle lands and their suitability for cultivation or other agricultural purposes in the various localities and what, if any, profitable use may be made of them through the introduction of livestock or crops adapted to such soils, by individuals or on a community plan, with notations of elevation, topography, temperatures, and seasonal conditions affecting production of fruit, cotton, and other crops; and (xvii) any other information or studies that may seem advisable in determining methods for the betterment of agricultural conditions and the development of the agricultural resources of the Commonwealth.",-1),c=e("p",null,"The Station may and it is contemplated that the Station will work in conjunction with and cooperate with similar agencies of the federal government to make such agricultural survey whenever a suitable and satisfactory arrangement can be made for such cooperation.",-1),l=e("p",null,[a("1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0433",target:"_blank",rel:"noreferrer"},"433"),a(", § 23-132.10; 2016, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588",target:"_blank",rel:"noreferrer"},"588"),a(".")],-1),d=[r,s,c,l];function u(h,m,f,p,v,g){return o(),i("div",null,d)}const _=t(n,[["render",u]]);export{b as __pageData,_ as default};
