import{_ as e,o as t,c as r,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-2626  Powers and duties of the Center.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2626.md","filePath":"vacode/23.1-2626.md"}'),a={name:"vacode/23.1-2626.md"},i=n('<h1 id="§-23-1-2626-powers-and-duties-of-the-center" tabindex="-1">§ 23.1-2626 Powers and duties of the Center. <a class="header-anchor" href="#§-23-1-2626-powers-and-duties-of-the-center" aria-label="Permalink to &quot;§ 23.1-2626  Powers and duties of the Center.&quot;">​</a></h1><p>The Center, under the direction of the executive director, shall:</p><ol><li><p>Develop a degree program in energy production and conservation research at the master&#39;s level in conjunction with the Council;</p></li><li><p>Develop and provide programs of continuing education and in-service training for persons who work in the fields of coal or other energy research, development, or production;</p></li><li><p>Collaborate with other departments of the University, including the Department of Mining and Minerals Engineering;</p></li><li><p>Conduct research in the fields of coal, coal utilization, migrating natural gases such as methane and propane, and other energy-related work;</p></li><li><p>Collect and maintain data on energy production, development, and utilization;</p></li><li><p>Foster the utilization of research information, discoveries, and data;</p></li><li><p>Coordinate the functions of the Center with each of the Center&#39;s energy research facilities to prevent duplication of effort;</p></li><li><p>Apply for and accept grants from the federal government, state government, and any other source to carry out the purposes of this article. The Center may comply with such conditions and execute such agreements as may be necessary to accept such grants;</p></li><li><p>Accept gifts, bequests, and any other thing of value to carry out the purposes of this article;</p></li><li><p>Receive, administer, and expend all funds and other assistance made available to the Center to carry out the purposes of this article;</p></li><li><p>Consult with the Division of Renewable Energy and Energy Efficiency of the Department of Energy in the preparation of the Virginia Energy Plan pursuant to § <a href="/vacode/45.2-1710/">45.2-1710</a>; and</p></li><li><p>Do all things necessary or convenient for the proper administration of this article.</p></li></ol><p>1977, c. 543, § 23-135.7:6; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0939" target="_blank" rel="noreferrer">939</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0532" target="_blank" rel="noreferrer">532</a>.</p>',4),o=[i];function s(l,c,p,h,d,f){return t(),r("div",null,o)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
