import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-2806 Richard Bland College.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2806.md","filePath":"vacode/23.1-2806.md"}'),t={name:"vacode/23.1-2806.md"},l=o('<h1 id="§-23-1-2806-richard-bland-college" tabindex="-1">§ 23.1-2806 Richard Bland College. <a class="header-anchor" href="#§-23-1-2806-richard-bland-college" aria-label="Permalink to &quot;§ 23.1-2806 Richard Bland College.&quot;">​</a></h1><p>A. Richard Bland College is a separate college under the supervision, management, and control of the board. Richard Bland College shall report to the board in such manner as the board may coordinate and direct.</p><p>B. The board shall establish and publish bylaws for Richard Bland College that define the school&#39;s functions.</p><p>C. All property, property rights, duties, contracts, and agreements of Richard Bland College are vested in the board.</p><p>D. The board shall designate a chief executive officer of Richard Bland College.</p><p>E. The board shall care for and preserve all property belonging to Richard Bland College.</p><p>F. The board shall (i) fix tuition, mandatory fees, and other necessary charges; (ii) appoint, remove, and define the responsibilities of the chief executive officer; and (iii) make such rules and regulations as it deems appropriate for Richard Bland College.</p><p>1958, c. 15; 1960, cc. 56, 180, § 23-49.1; 1962, cc. 69, 610; 1966, cc. 14, 707; 1976, c. 21; 1992, c. 103; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',8),i=[l];function n(d,c,s,h,p,f){return a(),r("div",null,i)}const u=e(t,[["render",n]]);export{g as __pageData,u as default};
