import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-2903 State Board; officers, meetings, and regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2903.md","filePath":"vacode/23.1-2903.md"}'),o={name:"vacode/23.1-2903.md"},i=r('<h1 id="§-23-1-2903-state-board-officers-meetings-and-regulations" tabindex="-1">§ 23.1-2903 State Board; officers, meetings, and regulations. <a class="header-anchor" href="#§-23-1-2903-state-board-officers-meetings-and-regulations" aria-label="Permalink to &quot;§ 23.1-2903 State Board; officers, meetings, and regulations.&quot;">​</a></h1><p>A. The State Board shall elect a chairman from its membership and may provide for the election of one of its members as vice-chairman.</p><p>B. The State Board shall meet at least four times annually and on the call of the chairman when in his opinion additional meetings are expedient or necessary.</p><p>C. Eight members of the State Board shall constitute a quorum for all purposes.</p><p>D. The main office of the State Board shall be in the Commonwealth.</p><p>E. The State Board may adopt necessary regulations for carrying out the purposes of this chapter.</p><p>1966, c. 679, § 23-217; 1980, c. 728; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0652" target="_blank" rel="noreferrer">652</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>.</p>',7),n=[i];function s(l,c,h,d,f,p){return a(),t("div",null,n)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
