import{_ as e,o as t,c as r,O as s}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 23.1-2906.2 Certain comprehensive community colleges; veterans advisors and veterans resource centers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2906.2.md","filePath":"vacode/23.1-2906.2.md"}'),a={name:"vacode/23.1-2906.2.md"},n=s('<h1 id="§-23-1-2906-2-certain-comprehensive-community-colleges-veterans-advisors-and-veterans-resource-centers" tabindex="-1">§ 23.1-2906.2 Certain comprehensive community colleges; veterans advisors and veterans resource centers. <a class="header-anchor" href="#§-23-1-2906-2-certain-comprehensive-community-colleges-veterans-advisors-and-veterans-resource-centers" aria-label="Permalink to &quot;§ 23.1-2906.2 Certain comprehensive community colleges; veterans advisors and veterans resource centers.&quot;">​</a></h1><p>A. Each of the seven comprehensive community colleges with the highest number of enrolled students who are veterans shall employ at least one full-time veterans advisor to provide comprehensive and intensive enrollment and advising services to current and prospective students who are veterans.</p><p>B. Each of the seven comprehensive community colleges with the highest number of enrolled students who are veterans shall establish a veterans resource center on campus to:</p><ol><li><p>Provide access to federal and state veterans resources;</p></li><li><p>Serve as a quiet place for veterans to study;</p></li><li><p>Enable veterans to connect to other veterans, helping them renew the bonds of military service; and</p></li><li><p>Be the central hub for all activities on campus related to veterans.</p></li></ol><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',5),o=[n];function i(c,l,v,d,h,p){return t(),r("div",null,o)}const _=e(a,[["render",i]]);export{u as __pageData,_ as default};
