import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-2907 Policy for the award of academic credit for military training.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2907.md","filePath":"vacode/23.1-2907.md"}'),r={name:"vacode/23.1-2907.md"},o=t('<h1 id="§-23-1-2907-policy-for-the-award-of-academic-credit-for-military-training" tabindex="-1">§ 23.1-2907 Policy for the award of academic credit for military training. <a class="header-anchor" href="#§-23-1-2907-policy-for-the-award-of-academic-credit-for-military-training" aria-label="Permalink to &quot;§ 23.1-2907 Policy for the award of academic credit for military training.&quot;">​</a></h1><p>A. The State Board shall adopt a policy for the award of academic credit to any student enrolled in a comprehensive community college who has successfully completed a military training course or program as part of his military service that is applicable to the student&#39;s certificate of degree requirements and is:</p><ol><li><p>Recommended for academic credit by a national higher education association that provides academic credit recommendations for military training courses or programs;</p></li><li><p>Noted on the student&#39;s military transcript issued by any of the Armed Forces of the United States; or</p></li><li><p>Otherwise documented in writing by any of the Armed Forces of the United States.</p></li></ol><p>B. The State Board shall:</p><ol><li><p>Develop a procedure for each comprehensive community college to receive the documentation necessary to identify and verify the military training course or program for which the student has applied for academic credit; and</p></li><li><p>Develop, maintain, and disseminate to each comprehensive community college a list of military training courses and programs that it has deemed qualified for the award of academic credit.</p></li></ol><p>C. Each comprehensive community college shall provide a copy of the State Board&#39;s policy for the award of academic credit for military training courses or programs to each student applicant.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0581" target="_blank" rel="noreferrer">581</a>, § 23-220.5; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',7),c=[o];function n(l,d,s,p,m,h){return a(),i("div",null,c)}const y=e(r,[["render",n]]);export{g as __pageData,y as default};
