import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 23.1-2911.1 Northern Virginia Community College; computer science training and professional development activities for public school teachers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-2911.1.md","filePath":"vacode/23.1-2911.1.md"}'),a={name:"vacode/23.1-2911.1.md"},n=o('<h1 id="§-23-1-2911-1-northern-virginia-community-college-computer-science-training-and-professional-development-activities-for-public-school-teachers" tabindex="-1">§ 23.1-2911.1 Northern Virginia Community College; computer science training and professional development activities for public school teachers. <a class="header-anchor" href="#§-23-1-2911-1-northern-virginia-community-college-computer-science-training-and-professional-development-activities-for-public-school-teachers" aria-label="Permalink to &quot;§ 23.1-2911.1 Northern Virginia Community College; computer science training and professional development activities for public school teachers.&quot;">​</a></h1><p>A. Northern Virginia Community College, in consultation with the Department of Education, shall contract with a partner organization to develop, market, and implement high-quality and effective computer science training and professional development activities for public school teachers throughout the Commonwealth for the purpose of improving the computer science literacy of all public school students in the Commonwealth.</p><p>B. Northern Virginia Community College shall also establish an advisory committee for the purpose of advising the college and its partner organization on the development, marketing, and implementation of training and professional development activities pursuant to subsection A. The Secretary of Commerce and Trade, the Secretary of Education, and the Secretary of Administration shall each submit to the college a list of names of qualified individuals, and the college shall appoint members to such advisory committee from such lists.</p><p>2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0779" target="_blank" rel="noreferrer">779</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>.</p>',4),r=[n];function c(l,s,h,p,m,d){return t(),i("div",null,r)}const g=e(a,[["render",c]]);export{f as __pageData,g as default};
