import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 23.1-3003 Board; duties and powers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3003.md","filePath":"vacode/23.1-3003.md"}'),r={name:"vacode/23.1-3003.md"},s=o('<h1 id="§-23-1-3003-board-duties-and-powers" tabindex="-1">§ 23.1-3003 Board; duties and powers. <a class="header-anchor" href="#§-23-1-3003-board-duties-and-powers" aria-label="Permalink to &quot;§ 23.1-3003 Board; duties and powers.&quot;">​</a></h1><p>A. The board shall generally direct the affairs of the Medical School and adopt such regulations and bylaws for its own government and procedures as it shall determine.</p><p>B. The board shall appoint a president of the Medical School who shall be the chief executive officer with such duties as may be prescribed by the board.</p><p>C. The board shall appoint a dean and a provost of the Medical School.</p><p>D. The board may appoint such vice presidents, administrative and academic officers, professors, teachers, staff members, agents, and other personnel as it deems proper and necessary for the transaction of its business within and outside the Commonwealth or the United States.</p><p>E. The board may confer degrees, including honorary degrees, consistent with the approval authority of the Council pursuant to § <a href="http://law.lis.virginia.gov/vacode/23.1-203/" target="_blank" rel="noreferrer">23.1-203</a>.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',7),i=[s];function n(d,c,h,p,l,f){return a(),t("div",null,i)}const m=e(r,[["render",n]]);export{u as __pageData,m as default};
