import{_ as e,o as i,c as n,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 23.1-305 Public institutions of higher education; targeted economic and innovation incentives.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-305.md","filePath":"vacode/23.1-305.md"}'),a={name:"vacode/23.1-305.md"},o=t('<h1 id="§-23-1-305-public-institutions-of-higher-education-targeted-economic-and-innovation-incentives" tabindex="-1">§ 23.1-305 Public institutions of higher education; targeted economic and innovation incentives. <a class="header-anchor" href="#§-23-1-305-public-institutions-of-higher-education-targeted-economic-and-innovation-incentives" aria-label="Permalink to &quot;§ 23.1-305 Public institutions of higher education; targeted economic and innovation incentives.&quot;">​</a></h1><p>A. The Governor shall consider and may recommend and the General Assembly shall consider and may fund targeted economic and innovation incentives to achieve the objective and purposes of this chapter. Such incentives may include incentives based on the economic opportunity metrics developed pursuant to subdivision B 4 of § <a href="http://law.lis.virginia.gov/vacode/23.1-309/" target="_blank" rel="noreferrer">23.1-309</a> and incentives for:</p><ol><li><p>Increased enrollment of Virginia students, in addition to the per student funding provided by § <a href="http://law.lis.virginia.gov/vacode/23.1-304/" target="_blank" rel="noreferrer">23.1-304</a>;</p></li><li><p>Increased degree completion for Virginia residents who have partial credit completion for a degree;</p></li><li><p>Increased degree completion in a timely or expedited manner;</p></li><li><p>Improved retention and graduation rates;</p></li><li><p>Increased degree production in STEM or other high-need areas such as the health care-related professions;</p></li><li><p>Increased research, including regional and public-private collaboration;</p></li><li><p>Optimal year-round utilization of resources and other efficiency reforms designed to reduce total institutional cost;</p></li><li><p>Technology-enhanced instruction, including course redesign, online instruction, and resource sharing among institutions; and</p></li><li><p>Enhanced comprehensive community college transfer programs and grants and other enhanced degree path programs.</p></li></ol><p>B. The Governor and the General Assembly shall consider maintenance of effort initiatives for individual institutions with unique missions and demonstrable performance in specific incentive areas identified pursuant to subsection A.</p><p>C. The criteria for measuring whether the incentive areas in subsection A have been met, and the benefits or consequences for meeting or not meeting such incentive areas, shall be developed and reviewed as provided in subdivisions B 3 and 4 of § <a href="http://law.lis.virginia.gov/vacode/23.1-309/" target="_blank" rel="noreferrer">23.1-309</a>.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0828" target="_blank" rel="noreferrer">828</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>, § 23-38.87:16; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',6),r=[o];function s(c,d,l,p,h,u){return i(),n("div",null,r)}const f=e(a,[["render",s]]);export{v as __pageData,f as default};
