import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-310 Assessment and certification of institutions by the Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-310.md","filePath":"vacode/23.1-310.md"}'),a={name:"vacode/23.1-310.md"},o=n('<h1 id="§-23-1-310-assessment-and-certification-of-institutions-by-the-council" tabindex="-1">§ 23.1-310 Assessment and certification of institutions by the Council. <a class="header-anchor" href="#§-23-1-310-assessment-and-certification-of-institutions-by-the-council" aria-label="Permalink to &quot;§ 23.1-310 Assessment and certification of institutions by the Council.&quot;">​</a></h1><p>The Council shall annually assess the degree to which each institution has satisfied any goals or criteria developed by the Higher Education Advisory Committee pursuant to § <a href="/vacode/23.1-309/">23.1-309</a> and no later than October 1 of each fiscal year provide a certified written report of the results of such annual assessment to the Governor and the Chairmen of the House Committees on Appropriations and Education and the Senate Committees on Finance and Appropriations and on Education and Health. In order to assist the Council in its assessment, each public institution of higher education, and each nonprofit private institution of higher education eligible for and seeking to qualify for state general funds, shall furnish periodic reports, including copies of institutional financial aid audit reports and audited financial statements, and such other pertinent information, including student-level data, as may be required by the Council.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0828" target="_blank" rel="noreferrer">828</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>, § 23-38.87:21; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',3),s=[o];function r(c,l,d,h,u,f){return t(),i("div",null,s)}const m=e(a,[["render",r]]);export{_ as __pageData,m as default};
