import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 23.1-3101 A.L. Philpott Manufacturing Extension Partnership established; purpose and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3101.md","filePath":"vacode/23.1-3101.md"}'),i={name:"vacode/23.1-3101.md"},r=t('<h1 id="§-23-1-3101-a-l-philpott-manufacturing-extension-partnership-established-purpose-and-duties" tabindex="-1">§ 23.1-3101 A.L. Philpott Manufacturing Extension Partnership established; purpose and duties. <a class="header-anchor" href="#§-23-1-3101-a-l-philpott-manufacturing-extension-partnership-established-purpose-and-duties" aria-label="Permalink to &quot;§ 23.1-3101 A.L. Philpott Manufacturing Extension Partnership established; purpose and duties.&quot;">​</a></h1><p>A. The A.L. Philpott Manufacturing Extension Partnership (the Extension Partnership), doing business as Genedge Alliance, is established as a political subdivision of the Commonwealth to help create and maintain industrial and manufacturing jobs. The Extension Partnership shall:</p><ol><li><p>Develop, demonstrate, test, and assist in the implementation of advanced manufacturing technologies;</p></li><li><p>Promote industrial expansion by providing manufacturing technology consulting services to manufacturers in the Commonwealth;</p></li><li><p>Foster the creation of manufacturing networks and the development of buyer and supplier relationships in the region and throughout the Commonwealth;</p></li><li><p>Serve as a resource center for industrial training and technology transfer programs for the renewal, enhancement, and expansion of existing manufacturing enterprises and manufacturing modernization outreach;</p></li><li><p>Be available as a federal demonstration center for the training of displaced workers in any manufacturing area; and</p></li><li><p>Receive and accept any available grants from any federal, state, or private agency, corporation, association, or person to be expended in fulfilling the duties enumerated in this subsection.</p></li></ol><p>B. The Extension Partnership is a local or regional industrial or economic development authority or organization for purposes of the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.).</p><p>1992, cc. 217, 668, § 23-231.8; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0031" target="_blank" rel="noreferrer">31</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0383" target="_blank" rel="noreferrer">383</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0336" target="_blank" rel="noreferrer">336</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0324" target="_blank" rel="noreferrer">324</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0398" target="_blank" rel="noreferrer">398</a>.</p>',5),o=[r];function s(l,p,c,h,d,u){return a(),n("div",null,o)}const m=e(i,[["render",s]]);export{f as __pageData,m as default};
