import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-3105 Powers of the board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3105.md","filePath":"vacode/23.1-3105.md"}'),o={name:"vacode/23.1-3105.md"},n=r('<h1 id="§-23-1-3105-powers-of-the-board" tabindex="-1">§ 23.1-3105 Powers of the board. <a class="header-anchor" href="#§-23-1-3105-powers-of-the-board" aria-label="Permalink to &quot;§ 23.1-3105 Powers of the board.&quot;">​</a></h1><p>In order to carry out the purposes of the Extension Partnership, the board may:</p><ol><li><p>Apply for, accept, and expend gifts, grants, or donations from public or private sources to enable the Extension Partnership to carry out its purposes;</p></li><li><p>Fix, alter, charge, and collect rates, fees, and other charges for the sale of the products of and services rendered by the Extension Partnership at rates determined by the board to pay the expenses of the Extension Partnership;</p></li><li><p>Make and enter into all contracts or agreements necessary or incidental to the performance of its duties and the execution of powers granted by this article, including agreements with any federal agency, person, private firm, or other organization that can provide technical or other business assistance to the Extension Partnership&#39;s industrial clients;</p></li><li><p>Employ consultants, researchers, architects, engineers, accountants, financial experts, investment bankers, superintendents, managers, and such other employees and agents as may be necessary and fix their compensation to be payable from funds made available to the Extension Partnership;</p></li><li><p>Render advice and assistance and provide services to state and federal agencies, local and regional economic development entities, private firms, and other persons or organizations providing services or facilities for small and medium-sized manufacturers and industrial firms in the Commonwealth;</p></li><li><p>Develop and provide programs or projects alone or in cooperation with any person, state or federal agency, state, local, or regional economic development entity, private firm, or other organization for economic development through improvements in industrial competitiveness in the Commonwealth; and</p></li><li><p>Do all acts and things necessary or convenient to carry out the powers granted to it by this article or any other act.</p></li></ol><p>1992, cc. 217, 668, § 23-231.11; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0324" target="_blank" rel="noreferrer">324</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',4),i=[n];function s(c,l,p,d,h,f){return t(),a("div",null,i)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
