import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 23.1-3107 Institute for Advanced Learning and Research established; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3107.md","filePath":"vacode/23.1-3107.md"}'),a={name:"vacode/23.1-3107.md"},r=n('<h1 id="§-23-1-3107-institute-for-advanced-learning-and-research-established-duties" tabindex="-1">§ 23.1-3107 Institute for Advanced Learning and Research established; duties. <a class="header-anchor" href="#§-23-1-3107-institute-for-advanced-learning-and-research-established-duties" aria-label="Permalink to &quot;§ 23.1-3107 Institute for Advanced Learning and Research established; duties.&quot;">​</a></h1><p>A. The Institute for Advanced Learning and Research (the Institute) is established in Southside Virginia as a political subdivision of the Commonwealth.</p><p>B. The Institute shall:</p><ol><li><p>Seek to diversify the economy of the Dan River region by engaging the resources of Virginia Polytechnic Institute and State University in partnership with Danville Community College and Averett University and public and private bodies and organizations of the region and Commonwealth;</p></li><li><p>Serve as a catalyst for economic and community transformation by leveraging and brokering resources that support the economic diversity of the Dan River region, particularly within the network economy;</p></li><li><p>Provide a site for the development of the technology and trained workforce necessary for new economic enterprises to flourish in Southside Virginia through the teaching, research, outreach, and technology available from its partner institutions;</p></li><li><p>Expand access to higher education in Southside Virginia by providing for adult and continuing education, workforce training and development, and degree-granting programs, including undergraduate, graduate, and professional programs, through partnerships with the Commonwealth&#39;s public institutions of higher education and private institutions of higher education, the City of Danville, Pittsylvania County, and the public schools and the public and private sectors in the region;</p></li><li><p>Serve as a resource and hub for network-related initiatives at all levels of education and in economic development activities;</p></li><li><p>Assist in regional economic and community development efforts by housing and encouraging research and product-related activities and encouraging high-technology economic development in the region;</p></li><li><p>Encourage and coordinate, as appropriate, the development and delivery of programs offered by the educational institutions serving the region; and</p></li><li><p>Serve as a resource and referral center by maintaining and disseminating information on existing educational programs, research, and university outreach resources.</p></li></ol><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0581" target="_blank" rel="noreferrer">581</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0620" target="_blank" rel="noreferrer">620</a>, § 23-231.19; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),o=[r];function s(d,c,l,h,g,p){return i(),t("div",null,o)}const v=e(a,[["render",s]]);export{f as __pageData,v as default};
