import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 23.1-3111 New College Institute established; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3111.md","filePath":"vacode/23.1-3111.md"}'),n={name:"vacode/23.1-3111.md"},o=a('<h1 id="§-23-1-3111-new-college-institute-established-duties" tabindex="-1">§ 23.1-3111 New College Institute established; duties. <a class="header-anchor" href="#§-23-1-3111-new-college-institute-established-duties" aria-label="Permalink to &quot;§ 23.1-3111 New College Institute established; duties.&quot;">​</a></h1><p>A. New College Institute (New College) is established as an educational institution of the Commonwealth in the area of Henry County and the City of Martinsville.</p><p>B. New College shall:</p><ol><li><p>Seek to diversify the region&#39;s economy by engaging the resources of other institutions of higher education, public and private bodies, and organizations of the region and Commonwealth;</p></li><li><p>Serve as a catalyst for economic and community transformation by leveraging and brokering resources that support economic diversity;</p></li><li><p>Facilitate development of the technology and trained workforce necessary for new economic enterprises to flourish, using the resources available from collaborating educational institutions;</p></li><li><p>Expand educational opportunities in the region by providing access to degree-granting programs, including undergraduate, graduate, and professional programs, through partnerships with private institutions of higher education and public institutions of higher education, the public schools, and the public and private sectors;</p></li><li><p>Encourage and coordinate the development and delivery of degree programs and other credit and noncredit courses with a focus on statewide and regional critical shortage areas and the needs of industry. Such programs and courses shall include needed adult education and workforce training; and</p></li><li><p>Serve as a resource and referral center by maintaining and disseminating information on existing educational programs, research, and university outreach and technology resources.</p></li></ol><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0808" target="_blank" rel="noreferrer">808</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>, § 23-231.30; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),r=[o];function s(l,d,c,u,g,h){return t(),i("div",null,r)}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
