import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 23.1-3116 Duties of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3116.md","filePath":"vacode/23.1-3116.md"}'),n={name:"vacode/23.1-3116.md"},o=i('<h1 id="§-23-1-3116-duties-of-the-authority" tabindex="-1">§ 23.1-3116 Duties of the Authority. <a class="header-anchor" href="#§-23-1-3116-duties-of-the-authority" aria-label="Permalink to &quot;§ 23.1-3116 Duties of the Authority.&quot;">​</a></h1><p>The Authority shall:</p><ol><li><p>Expand access to higher education in the Roanoke Valley by providing for adult and continuing education and degree-granting programs, including undergraduate, graduate, and professional programs, through partnerships with the Commonwealth&#39;s public institutions of higher education and private institutions of higher education;</p></li><li><p>Serve as a resource and referral center on existing educational programs and resources by maintaining and disseminating information;</p></li><li><p>Develop, in coordination with the Council, specific goals for higher education access and availability in the Roanoke Valley; and</p></li><li><p>Accept, administer, and account for any state grant to a nonstate entity that may be provided in the name of the Roanoke Higher Education Center (the Center) or the Authority.</p></li></ol><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, § 23-231.14; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0360" target="_blank" rel="noreferrer">360</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',4),r=[o];function s(l,c,h,d,p,u){return t(),a("div",null,r)}const _=e(n,[["render",s]]);export{f as __pageData,_ as default};
