import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-3119 Executive director; staff.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3119.md","filePath":"vacode/23.1-3119.md"}'),i={name:"vacode/23.1-3119.md"},o=r('<h1 id="§-23-1-3119-executive-director-staff" tabindex="-1">§ 23.1-3119 Executive director; staff. <a class="header-anchor" href="#§-23-1-3119-executive-director-staff" aria-label="Permalink to &quot;§ 23.1-3119 Executive director; staff.&quot;">​</a></h1><p>A. From funds available for this purpose, the board may appoint an executive director for the Center who shall supervise and manage the Center and prepare and submit, upon the direction and approval of the board, all requests for appropriations. The executive director of the Center may employ such staff as necessary to enable the Center to perform its duties as set forth in the bylaws of the board and this article. The board may determine the duties of the staff and fix salaries and compensation from such funds as may be appropriated or received.</p><p>B. Additional staff support for the functions of the Center may be provided upon agreement by the participating institutions.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, § 23-231.17; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',4),n=[o];function s(c,f,p,d,l,h){return t(),a("div",null,n)}const v=e(i,[["render",s]]);export{_ as __pageData,v as default};
