import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-3212 Duties of the board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3212.md","filePath":"vacode/23.1-3212.md"}'),i={name:"vacode/23.1-3212.md"},n=a('<h1 id="§-23-1-3212-duties-of-the-board" tabindex="-1">§ 23.1-3212 Duties of the board. <a class="header-anchor" href="#§-23-1-3212-duties-of-the-board" aria-label="Permalink to &quot;§ 23.1-3212 Duties of the board.&quot;">​</a></h1><p>The board shall seek to:</p><ol><li><p>Deepen our understanding of man and his environment;</p></li><li><p>Promote a knowledge of the scientific method and thus encourage objectivity in the everyday affairs of man;</p></li><li><p>Engage in instruction and research in the sciences in order to educate citizens of all ages in the concepts and principles of science and how these concepts and principles form the foundation upon which rests our technological society and its economy;</p></li><li><p>Use, subject to approval of the accredited educational affiliates concerned, Museum personnel in educational programs;</p></li><li><p>Motivate and stimulate young people to seek careers in science;</p></li><li><p>Encourage an understanding of the history of scientific endeavor;</p></li><li><p>Provide special facilities and collections for the study of the Commonwealth&#39;s natural resources; and</p></li><li><p>Foster a love of nature and concern for its preservation.</p></li></ol><p>Code 1950, § 9-65.2, § 23-240; 1970, c. 466; 1977, c. 597; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',4),r=[n];function s(c,l,d,p,h,f){return o(),t("div",null,r)}const m=e(i,[["render",s]]);export{_ as __pageData,m as default};
