import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 23.1-3223 Duties of the Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-3223.md","filePath":"vacode/23.1-3223.md"}'),o={name:"vacode/23.1-3223.md"},n=i('<h1 id="§-23-1-3223-duties-of-the-commission" tabindex="-1">§ 23.1-3223 Duties of the Commission. <a class="header-anchor" href="#§-23-1-3223-duties-of-the-commission" aria-label="Permalink to &quot;§ 23.1-3223 Duties of the Commission.&quot;">​</a></h1><p>A. The Commission shall:</p><ol><li><p>Stimulate and encourage throughout the Commonwealth growth in artistic quality and excellence, public interest and participation in the arts, and access to high-quality and affordable literary, visual, and performing arts for all Virginians;</p></li><li><p>Make recommendations concerning appropriate methods to encourage economic viability, an intellectually stimulating environment for artists, and participation in and appreciation of the arts to meet the legitimate needs and aspirations of persons in all parts of the Commonwealth;</p></li><li><p>Promote the development and implementation of a planned, sequential, and comprehensive program of arts education, taught by licensed teachers endorsed in arts education, in the public elementary and secondary schools of the Commonwealth;</p></li><li><p>Provide supplemental learning opportunities to the public school arts education curriculum;</p></li><li><p>Encourage the development of a network of professional arts organizations, the media, and arts promoters for the production of classical and new works of art and diversity in artistic expressions in media including the literary, visual, and performing arts;</p></li><li><p>Provide funding for and technical assistance to artists, recognized nonprofit arts organizations, and arts organizations and activities that celebrate and preserve the various cultures represented among the citizens of the Commonwealth;</p></li><li><p>Encourage and support the creation of new works of art, arts organizations whose primary objective is to increase public access to the arts, particularly in underserved areas, and performing arts tours to increase the availability of this form of artistic expression throughout the Commonwealth;</p></li><li><p>Establish a program of financial assistance to provide scholarships, grants, and other awards to artists who demonstrate exceptional ability and talent;</p></li><li><p>Establish an advisory panel composed of artists, arts administrators, and citizens to advise the Commission concerning fiscal matters;</p></li><li><p>Encourage arts organizations to dedicate to their endowments at least $1 of the price of each adult admission to performances or exhibitions or at least one percent of moneys collected in fund campaigns;</p></li><li><p>Encourage arts organizations to develop and implement endowment enlargement plans that yield enough income to underwrite one-third of the organizations&#39; annual operating costs;</p></li><li><p>Apply to and enter into contracts and agreements with the United States or any appropriate agency or officer of the United States for participation in or receipt of funding from any federal program respecting the arts;</p></li><li><p>Provide incentives to local governing bodies to encourage public support and funding of the arts;</p></li><li><p>Accept, hold, and administer gifts, contributions, and bequests of money or any other thing to be used for carrying out the purposes of this article;</p></li><li><p>Develop specific procedures for the administration and implementation of a grantmaking program, so long as any such program is for the benefit of a nonprofit organization qualifying as a § 501(c)(3) organization under the Internal Revenue Code and prepare written guidelines to govern such program;</p></li><li><p>Administer any funds available to the Commission and disburse such funds in accordance with the purposes of this article;</p></li><li><p>Make expenditures from the Fund&#39;s interest and income to promote the arts in the Commonwealth in accordance with § <a href="/vacode/23.1-3228/">23.1-3228</a> and assist nonprofit arts and cultural institutions and organizations in the Commonwealth to assess, enhance, and plan for enhancement of their fiscal stability, financial management and control capabilities, and capacity to raise funds for the furtherance of their respective missions from nongovernmental sources;</p></li><li><p>Enter into contracts and execute all instruments necessary and appropriate to carry out the Commission&#39;s purposes;</p></li><li><p>Explore and make recommendations concerning other possible dedicated revenue sources for the Fund; and</p></li><li><p>Perform any lawful acts necessary or appropriate to carry out the purposes of the Commission.</p></li></ol><p>B. Nothing in this article shall be construed to affect the statutory purposes of the Virginia Museum of Fine Arts.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0437" target="_blank" rel="noreferrer">437</a>.</p>',5),r=[n];function s(l,c,p,d,h,m){return t(),a("div",null,r)}const g=e(o,[["render",s]]);export{f as __pageData,g as default};
