import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 23.1-505 Determination of domicile; exception; dependents of certain active duty military personnel, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-505.md","filePath":"vacode/23.1-505.md"}'),a={name:"vacode/23.1-505.md"},n=i('<h1 id="§-23-1-505-determination-of-domicile-exception-dependents-of-certain-active-duty-military-personnel-etc" tabindex="-1">§ 23.1-505 Determination of domicile; exception; dependents of certain active duty military personnel, etc. <a class="header-anchor" href="#§-23-1-505-determination-of-domicile-exception-dependents-of-certain-active-duty-military-personnel-etc" aria-label="Permalink to &quot;§ 23.1-505 Determination of domicile; exception; dependents of certain active duty military personnel, etc.&quot;">​</a></h1><p>A. For the purposes of this section:</p><p>&quot;Date of alleged entitlement&quot; means the date of admission or acceptance for dependents currently residing in the Commonwealth or the final add/drop date for dependents of members newly transferred to the Commonwealth.</p><p>&quot;Temporarily mobilized&quot; means activated for service for 180 days or more.</p><p>&quot;Unaccompanied orders&quot; means orders that assign active duty military personnel or activated or temporarily mobilized reserve or guard members an unaccompanied tour listed in Appendix Q of the Joint Federal Travel Regulations.</p><p>B. Notwithstanding § <a href="/vacode/23.1-502/">23.1-502</a> or any other provision of law to the contrary, all dependents, as defined by 37 U.S.C. § 401, of active duty military personnel or activated or temporarily mobilized reservists or guard members (i) assigned to a permanent duty station or workplace in the Commonwealth, the District of Columbia, or a state contiguous to the Commonwealth who reside in the Commonwealth; (ii) assigned unaccompanied orders and immediately prior to receiving such unaccompanied orders were assigned to a permanent duty station or workplace in the Commonwealth, the District of Columbia, or a state contiguous to the Commonwealth and resided in the Commonwealth; or (iii) assigned unaccompanied orders with the Commonwealth listed as the designated place move shall be deemed to be domiciled in the Commonwealth and are eligible to receive in-state tuition.</p><p>C. All such dependents shall be afforded the same educational benefits as any other individual who is eligible for in-state tuition pursuant to § <a href="/vacode/23.1-502/">23.1-502</a>. Such dependents are eligible for such benefits, including in-state tuition status, for as long as they are continuously enrolled in a public institution of higher education or private institution of higher education or have transferred between public institutions of higher education or private institutions of higher education or from an undergraduate degree program to a graduate degree program at a public institution of higher education or private institution of higher education, regardless of any change of duty station or residence of the military service member. Such continuous enrollment requirement shall be waived if the dependent verifies that a break of no longer than one year was required in order to support a spouse or parent on orders for a change of duty assignment or location.</p><p>1984, c. 422, § 23-7.4; 1985, cc. 179, 572; 1988, c. 124; 1989, c. 371; 1990, c. 680; 1991, c. 590; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0931" target="_blank" rel="noreferrer">931</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0439" target="_blank" rel="noreferrer">439</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0797" target="_blank" rel="noreferrer">797</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0806" target="_blank" rel="noreferrer">806</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0108" target="_blank" rel="noreferrer">108</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0491" target="_blank" rel="noreferrer">491</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0723" target="_blank" rel="noreferrer">723</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0038" target="_blank" rel="noreferrer">38</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0044" target="_blank" rel="noreferrer">44</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0782" target="_blank" rel="noreferrer">782</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0139" target="_blank" rel="noreferrer">139</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0166" target="_blank" rel="noreferrer">166</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0243" target="_blank" rel="noreferrer">243</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0341" target="_blank" rel="noreferrer">341</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0382" target="_blank" rel="noreferrer">382</a>.</p>',8),o=[n];function l(s,c,d,g,p,f){return r(),t("div",null,o)}const m=e(a,[["render",l]]);export{u as __pageData,m as default};
