import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 23.1-508 Special arrangement contracts; reduced rate tuition charges.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-508.md","filePath":"vacode/23.1-508.md"}'),a={name:"vacode/23.1-508.md"},n=i('<h1 id="§-23-1-508-special-arrangement-contracts-reduced-rate-tuition-charges" tabindex="-1">§ 23.1-508 Special arrangement contracts; reduced rate tuition charges. <a class="header-anchor" href="#§-23-1-508-special-arrangement-contracts-reduced-rate-tuition-charges" aria-label="Permalink to &quot;§ 23.1-508 Special arrangement contracts; reduced rate tuition charges.&quot;">​</a></h1><p>A. Public institutions of higher education may enter into special arrangement contracts with employers in the Commonwealth or authorities controlling federal installations or agencies located in the Commonwealth for the purpose of providing reduced rate tuition charges for the employees of such employers or authorities who are non-Virginia students at such institutions when such employers or authorities assume the liability for paying, to the extent permitted by federal law, the tuition charges for such employees.</p><p>B. Such special arrangement contracts may be (i) for group instruction in facilities provided by the employer or federal authority or in the institution&#39;s facilities or (ii) on a student-by-student basis for specific employment-related programs.</p><p>C. Special arrangement contracts are valid for a period not to exceed two years and shall be reviewed for legal sufficiency by the Office of the Attorney General prior to signing. All tuition charges agreed to by the public institutions shall be at least equal to in-state tuition and shall be granted only by the institution with which the employer or the federal authorities have a valid contract for students for whom the employer or federal authority is paying the tuition charges.</p><p>D. All special arrangement contracts with authorities controlling federal installations or agencies shall include a specific number of students to be charged reduced tuition rates.</p><p>E. Nothing in this section shall change the domicile of any student for the purposes of enrollment reporting or calculating the proportions of general funds and tuition and fees contributed to the cost of education.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0931" target="_blank" rel="noreferrer">931</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>, § 23-7.4:2; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0062" target="_blank" rel="noreferrer">62</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0424" target="_blank" rel="noreferrer">424</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0437" target="_blank" rel="noreferrer">437</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0196" target="_blank" rel="noreferrer">196</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0483" target="_blank" rel="noreferrer">483</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0501" target="_blank" rel="noreferrer">501</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0520" target="_blank" rel="noreferrer">520</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0797" target="_blank" rel="noreferrer">797</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0076" target="_blank" rel="noreferrer">76</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0102" target="_blank" rel="noreferrer">102</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0112" target="_blank" rel="noreferrer">112</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0723" target="_blank" rel="noreferrer">723</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0470" target="_blank" rel="noreferrer">470</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0376" target="_blank" rel="noreferrer">376</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0166" target="_blank" rel="noreferrer">166</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0243" target="_blank" rel="noreferrer">243</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0302" target="_blank" rel="noreferrer">302</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0341" target="_blank" rel="noreferrer">341</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0762" target="_blank" rel="noreferrer">762</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',7),l=[n];function o(g,c,s,h,f,p){return r(),t("div",null,l)}const b=e(a,[["render",o]]);export{d as __pageData,b as default};
