import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 23.1-601 Public institutions of higher education; grants for tuition and fees for certain individuals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-601.md","filePath":"vacode/23.1-601.md"}'),r={name:"vacode/23.1-601.md"},n=t('<h1 id="§-23-1-601-public-institutions-of-higher-education-grants-for-tuition-and-fees-for-certain-individuals" tabindex="-1">§ 23.1-601 Public institutions of higher education; grants for tuition and fees for certain individuals. <a class="header-anchor" href="#§-23-1-601-public-institutions-of-higher-education-grants-for-tuition-and-fees-for-certain-individuals" aria-label="Permalink to &quot;§ 23.1-601 Public institutions of higher education; grants for tuition and fees for certain individuals.&quot;">​</a></h1><p>A. Each comprehensive community college shall and any other associate-degree-granting public institution of higher education or baccalaureate public institution of higher education may provide a grant for the payment of tuition and fees, except fees established for the purpose of paying for course materials such as laboratory fees, for any Virginia student who:</p><ol><li>a. Has received a high school diploma or has passed a high school equivalency examination approved by the Board of Education and was in foster care or in the custody of the Department of Social Services or is considered a special needs adoption at the time such diploma or certificate was awarded; or</li></ol><p>b. Was in foster care when he turned 18 and subsequently received a high school diploma or passed a high school equivalency examination approved by the Board of Education;</p><ol start="2"><li><p>Is enrolled or has been accepted for enrollment as a full-time or part-time student, taking a minimum of six credit hours per semester, in a degree or certificate program of at least one academic year in length or in a noncredit workforce credential program in a comprehensive community college;</p></li><li><p>Has not been enrolled in postsecondary education as a full-time student for more than five years or does not have a bachelor&#39;s degree;</p></li><li><p>Maintains the required grade point average established by the governing board of the institution at which he is enrolled;</p></li><li><p>Has submitted complete applications for federal student financial aid programs for which he may be eligible;</p></li><li><p>Demonstrates financial need; and</p></li><li><p>Meets any additional financial need requirements established by the governing board of the institution at which he is enrolled for the purposes of such grant.</p></li></ol><p>B. The State Board and the Council, in consultation with the Department of Social Services, shall establish regulations governing such grants. The regulations shall include provisions addressing renewals of grants, financial need, the calculation of grant amounts after consideration of any additional financial resources or aid the student holds, the minimum grade point average required to retain such grant, and procedures for the repayment of tuition and fees for failure to meet the requirements imposed by this section.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0968" target="_blank" rel="noreferrer">968</a>, § 23-7.4:5; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0430" target="_blank" rel="noreferrer">430</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0603" target="_blank" rel="noreferrer">603</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0084" target="_blank" rel="noreferrer">84</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0306" target="_blank" rel="noreferrer">306</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0318" target="_blank" rel="noreferrer">318</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0589" target="_blank" rel="noreferrer">589</a>.</p>',7),o=[n];function s(l,c,d,h,f,u){return i(),a("div",null,o)}const m=e(r,[["render",s]]);export{p as __pageData,m as default};
