import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-607.1 Veterans; withdrawal; tuition refund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-607.1.md","filePath":"vacode/23.1-607.1.md"}'),n={name:"vacode/23.1-607.1.md"},r=i('<h1 id="§-23-1-607-1-veterans-withdrawal-tuition-refund" tabindex="-1">§ 23.1-607.1 Veterans; withdrawal; tuition refund. <a class="header-anchor" href="#§-23-1-607-1-veterans-withdrawal-tuition-refund" aria-label="Permalink to &quot;§ 23.1-607.1 Veterans; withdrawal; tuition refund.&quot;">​</a></h1><p>A. As used in this section, &quot;veteran&quot; has the same meaning as provided in § <a href="/vacode/23.1-500/">23.1-500</a>.</p><p>B. Each public institution of higher education shall provide a refund of the tuition and mandatory fees paid by any veteran student for any course from which such veteran student is forced to withdraw, for the first time, due to a service-connected medical condition during a semester, as certified in writing to the institution by a physician licensed to practice medicine pursuant to Chapter 29 (§ <a href="/vacode/54.1-2900/">54.1-2900</a> et seq.) of Title 54.1 who treated the veteran student for such medical condition. Such refund shall not be issued when three-quarters of a course has been completed at the time that the veteran student withdraws from the course. The time period that constitutes three-quarters of a course shall be determined by the institution.</p><p>C. Nothing in this section shall be construed to affect any such student&#39;s ability to reenroll at the institution.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0434" target="_blank" rel="noreferrer">434</a>.</p>',5),o=[r];function s(d,c,h,u,l,f){return t(),a("div",null,o)}const m=e(n,[["render",s]]);export{_ as __pageData,m as default};
