import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 23.1-609 Surviving spouses and children of certain individuals; tuition and fee waivers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-609.md","filePath":"vacode/23.1-609.md"}'),t={name:"vacode/23.1-609.md"},n=a('<h1 id="§-23-1-609-surviving-spouses-and-children-of-certain-individuals-tuition-and-fee-waivers" tabindex="-1">§ 23.1-609 Surviving spouses and children of certain individuals; tuition and fee waivers. <a class="header-anchor" href="#§-23-1-609-surviving-spouses-and-children-of-certain-individuals-tuition-and-fee-waivers" aria-label="Permalink to &quot;§ 23.1-609 Surviving spouses and children of certain individuals; tuition and fee waivers.&quot;">​</a></h1><p>A. The surviving spouse and any child between the ages of 16 and 25 of an individual who was killed in the line of duty while employed or serving as a (i) law-enforcement officer, including as a campus police officer appointed under Article 3 (§ <a href="/vacode/23.1-809/">23.1-809</a> et seq.) of Chapter 8, sworn law-enforcement officer, firefighter, special forest warden pursuant to § <a href="/vacode/10.1-1135/">10.1-1135</a>, member of a rescue squad, special agent of the Virginia Alcoholic Beverage Control Authority, state correctional, regional or local jail officer, regional jail or jail farm superintendent, sheriff, or deputy sheriff; (ii) member of the Virginia National Guard while serving on official state duty or federal duty under Title 32 of the United States Code; or (iii) member of the Virginia Defense Force while serving on official state duty, and any individual whose spouse was killed in the line of duty while employed or serving in any of such occupations, is entitled to a waiver of undergraduate tuition and mandatory fees at any public institution of higher education under the following conditions:</p><ol><li><p>The chief executive officer of the deceased individual&#39;s employer certifies that such individual was so employed and was killed in the line of duty while serving or living in the Commonwealth; and</p></li><li><p>The surviving spouse or child is admitted to, enrolls at, and is in attendance at such institution and applies to such institution for the waiver. Waiver recipients who make satisfactory academic progress are eligible for renewal of such waiver.</p></li></ol><p>B. Institutions that grant such waivers shall waive the amounts payable for tuition, institutional charges and mandatory educational and auxiliary fees, and books and supplies but shall not waive user fees such as room and board charges.</p><p>C. Each public institution of higher education shall include in its catalog or equivalent publication a statement describing the benefits available pursuant to this section.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0931" target="_blank" rel="noreferrer">931</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>, § 23-7.4:1; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0377" target="_blank" rel="noreferrer">377</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0330" target="_blank" rel="noreferrer">330</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0116" target="_blank" rel="noreferrer">116</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0161" target="_blank" rel="noreferrer">161</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0717" target="_blank" rel="noreferrer">717</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0776" target="_blank" rel="noreferrer">776</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0184" target="_blank" rel="noreferrer">184</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0038" target="_blank" rel="noreferrer">38</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0730" target="_blank" rel="noreferrer">730</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',6),l=[n];function o(s,g,f,c,h,d){return i(),r("div",null,l)}const v=e(t,[["render",o]]);export{p as __pageData,v as default};
