import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 23.1-627 State financial aid eligibility.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-627.md","filePath":"vacode/23.1-627.md"}'),n={name:"vacode/23.1-627.md"},r=a('<h1 id="§-23-1-627-state-financial-aid-eligibility" tabindex="-1">§ 23.1-627 State financial aid eligibility. <a class="header-anchor" href="#§-23-1-627-state-financial-aid-eligibility" aria-label="Permalink to &quot;§ 23.1-627 State financial aid eligibility.&quot;">​</a></h1><p>A. Eligible institutions shall reduce a student&#39;s state financial aid eligibility by the amount of the grant awarded pursuant to this article.</p><p>B. Grants shall not be reduced by virtue of an eligible student&#39;s receipt of any other financial aid from any other source except when the total of the grant and such other financial aid would enable the student to receive total financial assistance in excess of the estimated cost to the student of attending the institution in which he is enrolled.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0850" target="_blank" rel="noreferrer">850</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0899" target="_blank" rel="noreferrer">899</a>, § 23-38.10:13; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',4),l=[r];function o(s,c,d,h,f,_){return t(),i("div",null,l)}const u=e(n,[["render",o]]);export{p as __pageData,u as default};
