import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 23.1-631 Eligibility; duration.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-631.md","filePath":"vacode/23.1-631.md"}'),i={name:"vacode/23.1-631.md"},o=r('<h1 id="§-23-1-631-eligibility-duration" tabindex="-1">§ 23.1-631 Eligibility; duration. <a class="header-anchor" href="#§-23-1-631-eligibility-duration" aria-label="Permalink to &quot;§ 23.1-631 Eligibility; duration.&quot;">​</a></h1><p>A. Virginia students who are obligated to pay tuition as full-time undergraduate, graduate, or professional students at an eligible institution are eligible to receive a grant for the academic year for which they enroll.</p><p>B. Eligibility for grants under the Program is limited to a total of four academic years for undergraduate students, pharmacy students, and medical students and a total of three academic years for other graduate students and professional school students. The academic years for which grants are awarded need not be in succession.</p><p>C. Grants under the Program shall be used only for undergraduate, graduate, or professional collegiate work in educational programs other than those providing religious training or theological education.</p><p>1972, c. 18, §§ 23-38.15, 23-38.16; 1973, c. 2; 1975, c. 400; 1980, c. 101; 1981, c. 257; 1987, c. 600; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0094" target="_blank" rel="noreferrer">94</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0660" target="_blank" rel="noreferrer">660</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),n=[o];function l(s,d,c,g,u,h){return a(),t("div",null,n)}const _=e(i,[["render",l]]);export{p as __pageData,_ as default};
