import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-635 Determination of domicile; Council oversight and reports.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-635.md","filePath":"vacode/23.1-635.md"}'),o={name:"vacode/23.1-635.md"},r=n('<h1 id="§-23-1-635-determination-of-domicile-council-oversight-and-reports" tabindex="-1">§ 23.1-635 Determination of domicile; Council oversight and reports. <a class="header-anchor" href="#§-23-1-635-determination-of-domicile-council-oversight-and-reports" aria-label="Permalink to &quot;§ 23.1-635 Determination of domicile; Council oversight and reports.&quot;">​</a></h1><p>A. For the purposes of determining a student&#39;s eligibility for a grant, the enrolling institution shall determine domicile as provided in § <a href="http://law.lis.virginia.gov/vacode/23.1-502/" target="_blank" rel="noreferrer">23.1-502</a> and the Council&#39;s domicile guidelines.</p><p>B. In order to ensure consistency and fairness, the Council shall (i) require all participating eligible institutions to file student-specific data, (ii) monitor the decisions of such institutions regarding domicile, and (iii) make final decisions on any disputes between such institutions and grant applicants.</p><p>C. The Council shall report to the Governor and the General Assembly, as the Council deems necessary, on issues relating to determinations of domicile for students applying for grants.</p><p>1972, c. 18, § 23-38.18; 1973, c. 2; 1985, c. 359; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0663" target="_blank" rel="noreferrer">663</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),a=[r];function s(l,c,d,p,h,u){return i(),t("div",null,a)}const _=e(o,[["render",s]]);export{g as __pageData,_ as default};
