import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 23.1-638 Eligibility; amount of grants; renewals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-638.md","filePath":"vacode/23.1-638.md"}'),n={name:"vacode/23.1-638.md"},r=i('<h1 id="§-23-1-638-eligibility-amount-of-grants-renewals" tabindex="-1">§ 23.1-638 Eligibility; amount of grants; renewals. <a class="header-anchor" href="#§-23-1-638-eligibility-amount-of-grants-renewals" aria-label="Permalink to &quot;§ 23.1-638 Eligibility; amount of grants; renewals.&quot;">​</a></h1><p>A. Only students who (i) are accepted for full-time enrollment at a public institution of higher education, (ii) are not receiving a Virginia Commonwealth Award, (iii) demonstrate financial need as determined by the Council, and (iv) are either (a) Virginia students who graduated from a high school in the Commonwealth with a cumulative grade point average of at least 2.5 on a scale of 4.0 or its equivalent or (b) dependent children of active duty military personnel residing outside the Commonwealth pursuant to military orders, claiming Virginia on their State of Legal Residence Certificate, and satisfying the domicile requirements for such active duty military personnel pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/23.1-502/" target="_blank" rel="noreferrer">23.1-502</a> and <a href="http://law.lis.virginia.gov/vacode/23.1-504/" target="_blank" rel="noreferrer">23.1-504</a> and who graduated from a high school within or outside the Commonwealth with a cumulative grade point average of at least 2.5 on a scale of 4.0 or its equivalent are eligible to receive such grants.</p><p>B. Each eligible student shall receive such a grant from the institution&#39;s appropriations for undergraduate student financial assistance before Commonwealth Award grants are awarded to students with equivalent remaining need.</p><p>C. Each eligible student shall receive a grant in an amount greater than Commonwealth Award grants awarded to students with equivalent remaining need.</p><p>D. Beginning with first-time students enrolled in the fall semester in 2018, each eligible student shall receive a grant in an amount greater than the grant of each eligible student with equivalent remaining need in the next-lowest class level.</p><p>E. The amount of each grant shall vary according to each student&#39;s remaining need and the total of tuition, fees, and other necessary charges, including books. The actual amount of each grant shall be determined by the proportionate award schedule adopted by each institution.</p><p>F. All grants shall be awarded for one award year and may be renewed annually for no more than three subsequent award years, and students shall not receive subsequent grants until they have satisfied the requirements to move to the next class level. Each recipient may receive a maximum of one year of support per class level for a maximum total of four years of support at a baccalaureate public institution of higher education and a maximum total of two years of support at an associate-degree-granting public institution of higher education, provided that in no case shall a recipient receive more than a combined total of four years of support, if the recipient:</p><ol><li><p>Maintains a cumulative grade point average of at least 2.0 on a scale of 4.0 or its equivalent;</p></li><li><p>Demonstrates continued financial need;</p></li><li><p>Makes satisfactory academic progress toward a degree; and</p></li><li><p>Maintains continuous full-time enrollment for not less than two semesters or three quarters in each successive award year unless the Council grants the recipient an exception for cause.</p></li></ol><p>1992, c. 879, § 23-38.53:6; 1993, c. 832; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0114" target="_blank" rel="noreferrer">114</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0325" target="_blank" rel="noreferrer">325</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0335" target="_blank" rel="noreferrer">335</a>.</p>',9),o=[r];function l(s,c,h,d,u,g){return a(),t("div",null,o)}const m=e(n,[["render",l]]);export{p as __pageData,m as default};
