import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 23.1-703 Chief executive officer of the Plan.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-703.md","filePath":"vacode/23.1-703.md"}'),n={name:"vacode/23.1-703.md"},o=i('<h1 id="§-23-1-703-chief-executive-officer-of-the-plan" tabindex="-1">§ 23.1-703 Chief executive officer of the Plan. <a class="header-anchor" href="#§-23-1-703-chief-executive-officer-of-the-plan" aria-label="Permalink to &quot;§ 23.1-703 Chief executive officer of the Plan.&quot;">​</a></h1><p>A. The board shall employ a chief executive officer to direct, manage, and administer the Plan. The chief executive officer may employ such staff as are necessary to accomplish the Plan&#39;s stated objectives.</p><p>B. The chief executive officer shall demonstrate (i) extensive experience in some or all of the following areas: management, finance, law, regulatory affairs, and investments and (ii) such other qualifications as the board may set.</p><p>C. The chief executive officer shall, in addition to such other duties as the board may establish, (i) oversee the development, structure, evaluation, and implementation of the Plan&#39;s strategic goals and objectives; (ii) facilitate communication among and between the board, advisory committees, employees, account owners, beneficiaries, and outside entities interested in the Plan; (iii) enhance the board&#39;s ability to make effective and prompt decisions in all matters relating to the administration of the Plan; (iv) with the assistance of the Investment Advisory Committee appointed by the board and investment consultants, direct, manage, and administer the Plan&#39;s assets and programs; and (v) report to the board periodically and as requested by the board.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0661" target="_blank" rel="noreferrer">661</a>, § 23-38.79; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0827" target="_blank" rel="noreferrer">827</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0845" target="_blank" rel="noreferrer">845</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),r=[o];function s(c,l,f,h,d,m){return t(),a("div",null,r)}const u=e(n,[["render",s]]);export{v as __pageData,u as default};
