import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 23.1-704 Powers and duties of the board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-704.md","filePath":"vacode/23.1-704.md"}'),r={name:"vacode/23.1-704.md"},n=t('<h1 id="§-23-1-704-powers-and-duties-of-the-board" tabindex="-1">§ 23.1-704 Powers and duties of the board. <a class="header-anchor" href="#§-23-1-704-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 23.1-704 Powers and duties of the board.&quot;">​</a></h1><p>The board shall:</p><ol><li><p>Administer the Plan established by this chapter;</p></li><li><p>Develop and implement programs for (i) the prepayment of undergraduate tuition, as defined in § <a href="/vacode/23.1-700/">23.1-700</a>, at a fixed, guaranteed level for application at a public institution of higher education; (ii) contributions to college savings trust accounts established pursuant to this chapter on behalf of a qualified beneficiary in order to apply distributions from the account toward qualified higher education expenses, as that term is defined in § 529 of the Internal Revenue Code of 1986, as amended, or other applicable federal law; and (iii) contributions to ABLE savings trust accounts established pursuant to this chapter on behalf of a qualified beneficiary in order to apply distributions from the account toward qualified disability expenses for an eligible individual, as both such terms are defined in § 529A of the Internal Revenue Code of 1986, as amended, or other applicable federal law;</p></li><li><p>Invest moneys in the Plan in any instruments, obligations, securities, or property deemed appropriate by the board;</p></li><li><p>Develop requirements, procedures, and guidelines regarding prepaid tuition contracts and savings trust accounts, including residency and other eligibility requirements; the number of participants in the Plan; the termination, withdrawal, or transfer of payments under a prepaid tuition contract or savings trust account; time limitations for the use of tuition benefits or savings trust account distributions; and payment schedules;</p></li><li><p>Enter into contractual agreements, including contracts for legal, actuarial, financial, and consulting services and contracts with other states to provide savings trust accounts for residents of contracting states;</p></li><li><p>Procure insurance as determined appropriate by the board (i) against any loss in connection with the Plan&#39;s property, assets, or activities and (ii) indemnifying board members from personal loss or accountability from liability arising from any action or inaction as a board member;</p></li><li><p>Make arrangements with public institutions of higher education to fulfill obligations under prepaid tuition contracts and apply college savings trust account distributions, including (i) payment from the Plan of the appropriate amount of tuition on behalf of a qualified beneficiary of a prepaid tuition contract to the institution to which the beneficiary is admitted and at which the beneficiary is enrolled and (ii) application of such benefits toward graduate-level tuition and toward qualified higher education expenses, as that term is defined in 26 U.S.C. § 529 or any other applicable section of the Internal Revenue Code of 1986, as amended, as determined by the board in its sole discretion;</p></li><li><p>Develop and implement scholarship or matching grant programs, or both, as the board may deem appropriate, to further its goal of making higher education more affordable and accessible to all citizens of the Commonwealth;</p></li><li><p>Apply for, accept, and expend gifts, grants, or donations from public or private sources to enable it to carry out its objectives;</p></li><li><p>Adopt regulations and procedures and perform any act or function consistent with the purposes of this chapter; and</p></li><li><p>Reimburse, at its option, all or part of the cost of employing legal counsel and such other costs as are demonstrated to have been reasonably necessary for the defense of any board member, officer, or employee of the Plan upon the acquittal, dismissal of charges, nolle prosequi, or any other final disposition concluding the innocence of such member, officer, or employee who is brought before any regulatory body, summoned before any grand jury, investigated by any law-enforcement agency, arrested, indicted, or otherwise prosecuted on any criminal charge arising out of any act committed in the discharge of his official duties that alleges a violation of state or federal securities laws. The board shall provide for the payment of such legal fees and expenses out of funds appropriated or otherwise available to the board.</p></li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0661" target="_blank" rel="noreferrer">661</a>, § 23-38.77; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0785" target="_blank" rel="noreferrer">785</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0061" target="_blank" rel="noreferrer">61</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0085" target="_blank" rel="noreferrer">85</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0485" target="_blank" rel="noreferrer">485</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0518" target="_blank" rel="noreferrer">518</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0382" target="_blank" rel="noreferrer">382</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0400" target="_blank" rel="noreferrer">400</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0827" target="_blank" rel="noreferrer">827</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0845" target="_blank" rel="noreferrer">845</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0227" target="_blank" rel="noreferrer">227</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0311" target="_blank" rel="noreferrer">311</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>.</p>',4),o=[n];function s(l,c,d,p,f,h){return i(),a("div",null,o)}const b=e(r,[["render",s]]);export{u as __pageData,b as default};
