import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 23.1-707.1 Prepaid tuition contracts; pricing reserves; limitations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-707.1.md","filePath":"vacode/23.1-707.1.md"}'),n={name:"vacode/23.1-707.1.md"},r=a('<h1 id="§-23-1-707-1-prepaid-tuition-contracts-pricing-reserves-limitations" tabindex="-1">§ 23.1-707.1 Prepaid tuition contracts; pricing reserves; limitations. <a class="header-anchor" href="#§-23-1-707-1-prepaid-tuition-contracts-pricing-reserves-limitations" aria-label="Permalink to &quot;§ 23.1-707.1 Prepaid tuition contracts; pricing reserves; limitations.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Funded status&quot; means the ratio of the assets of the Plan to the actuarially estimated value of tuition obligations of the Plan, expressed as a percentage.</p><p>&quot;Pricing reserve&quot; means the percentage by which the actuarially determined prepaid tuition contract price exceeds the amount estimated to meet the actuarially determined tuition obligation for such prepaid tuition contract.</p><p>B. In the event that the funded status of the Plan meets or exceeds 105 percent, the pricing reserve shall not exceed five percent.</p><p>C. In the event that the funded status of the Plan does not meet or exceed 105 percent, the pricing reserve may exceed five percent but shall not exceed 10 percent.</p><p>D. The board shall provide to the House Committee on Appropriations, the Senate Committee on Finance and Appropriations, and the Joint Legislative Audit and Review Commission written notification and a detailed explanation of any change to the pricing reserve within 30 days of such change.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0806" target="_blank" rel="noreferrer">806</a>.</p>',8),o=[r];function s(c,p,d,h,l,u){return t(),i("div",null,o)}const f=e(n,[["render",s]]);export{_ as __pageData,f as default};
