import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-807 Sexual assault; memoranda of understanding; policies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-807.md","filePath":"vacode/23.1-807.md"}'),o={name:"vacode/23.1-807.md"},n=i('<h1 id="§-23-1-807-sexual-assault-memoranda-of-understanding-policies" tabindex="-1">§ 23.1-807 Sexual assault; memoranda of understanding; policies. <a class="header-anchor" href="#§-23-1-807-sexual-assault-memoranda-of-understanding-policies" aria-label="Permalink to &quot;§ 23.1-807 Sexual assault; memoranda of understanding; policies.&quot;">​</a></h1><p>A. Richard Bland College and each baccalaureate public institution of higher education and nonprofit private institution of higher education shall establish, and the State Board shall adopt a policy requiring each comprehensive community college to establish, a written memorandum of understanding with a sexual assault crisis center or other victim support service in order to provide sexual assault victims with immediate access to a confidential, independent advocate who can provide a trauma-informed response that includes an explanation of options for moving forward.</p><p>B. Each public institution of higher education and nonprofit private institution of higher education shall adopt policies to provide to sexual assault victims information on contacting such sexual assault crisis center or other victim support service.</p><p>C. Each public institution of higher education or nonprofit private institution of higher education may request the cooperation of the primary law-enforcement agency of the locality in which the institution is located to establish a written memorandum of understanding with such law-enforcement agency to address the prevention of and response to criminal sexual assault as set forth in Article 7 (§ <a href="http://law.lis.virginia.gov/vacode/18.2-61/" target="_blank" rel="noreferrer">18.2-61</a> et seq.) of Chapter 4 of Title 18.2.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0737" target="_blank" rel="noreferrer">737</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>, § 23-9.2:16; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0481" target="_blank" rel="noreferrer">481</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',5),r=[n];function s(l,c,d,u,h,p){return a(),t("div",null,r)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
