import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 23.1-809 Public institutions of higher education; establishment of campus police departments authorized; employment of officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-809.md","filePath":"vacode/23.1-809.md"}'),r={name:"vacode/23.1-809.md"},o=a('<h1 id="§-23-1-809-public-institutions-of-higher-education-establishment-of-campus-police-departments-authorized-employment-of-officers" tabindex="-1">§ 23.1-809 Public institutions of higher education; establishment of campus police departments authorized; employment of officers. <a class="header-anchor" href="#§-23-1-809-public-institutions-of-higher-education-establishment-of-campus-police-departments-authorized-employment-of-officers" aria-label="Permalink to &quot;§ 23.1-809 Public institutions of higher education; establishment of campus police departments authorized; employment of officers.&quot;">​</a></h1><p>A. The governing board of each public institution of higher education may establish a campus police department and employ campus police officers and auxiliary police forces upon appointment as provided in §§ <a href="http://law.lis.virginia.gov/vacode/23.1-811/" target="_blank" rel="noreferrer">23.1-811</a> and <a href="http://law.lis.virginia.gov/vacode/23.1-812/" target="_blank" rel="noreferrer">23.1-812</a>. Such employment is governed by the Virginia Personnel Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-2900/" target="_blank" rel="noreferrer">2.2-2900</a> et seq.), except that the governing board of a public institution of higher education may direct that the employment of the chief of the campus police department is not governed by the Virginia Personnel Act.</p><p>B. The Virginia Commonwealth University Health System Authority and Eastern Virginia Medical School may employ police officers and auxiliary police forces as provided in this article and, in the case of the Authority, in § <a href="http://law.lis.virginia.gov/vacode/23.1-2406/" target="_blank" rel="noreferrer">23.1-2406</a>, except that the employment of such officers and forces is not governed by the Virginia Personnel Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-2900/" target="_blank" rel="noreferrer">2.2-2900</a> et seq.).</p><p>1977, c. 79, § 23-232; 1991, c. 711; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0905" target="_blank" rel="noreferrer">905</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1046" target="_blank" rel="noreferrer">1046</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0720" target="_blank" rel="noreferrer">720</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0596" target="_blank" rel="noreferrer">596</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>.</p>',4),n=[o];function l(c,s,h,p,f,d){return t(),i("div",null,n)}const u=e(r,[["render",l]]);export{m as __pageData,u as default};
