import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 23.1-902 Education preparation programs offered by institutions of higher education.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-902.md","filePath":"vacode/23.1-902.md"}'),r={name:"vacode/23.1-902.md"},o=t('<h1 id="§-23-1-902-education-preparation-programs-offered-by-institutions-of-higher-education" tabindex="-1">§ 23.1-902 Education preparation programs offered by institutions of higher education. <a class="header-anchor" href="#§-23-1-902-education-preparation-programs-offered-by-institutions-of-higher-education" aria-label="Permalink to &quot;§ 23.1-902 Education preparation programs offered by institutions of higher education.&quot;">​</a></h1><p>A. Education preparation programs offered by public institutions of higher education and private institutions of higher education shall meet the requirements for accreditation and program approval as prescribed by the Board of Education in its regulations.</p><p>B. The Board of Education may prescribe in its regulations requirements for admission to approved education preparation programs in the Commonwealth.</p><p>C. Any candidate who fails to achieve the minimum score established by the Board of Education may be denied entrance into an education preparation program on the basis of such failure, but any such candidate who gains entrance and enrolls in an education preparation program shall have the opportunity to address all deficiencies.</p><p>D. Education preparation programs offered by public institutions of higher education and private institutions of higher education shall ensure that, as a condition of degree completion, each student enrolled in the education preparation program receives instruction on positive behavior interventions and supports; crisis prevention and de-escalation; the use of physical restraint and seclusion, consistent with regulations of the Board of Education; and appropriate alternative methods to reduce and prevent the need for the use of physical restraint and seclusion.</p><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0027" target="_blank" rel="noreferrer">27</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0349" target="_blank" rel="noreferrer">349</a>, § 23-9.2:3.6; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0063" target="_blank" rel="noreferrer">63</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0407" target="_blank" rel="noreferrer">407</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0870" target="_blank" rel="noreferrer">870</a>.</p>',6),n=[o];function s(p,c,d,h,l,u){return a(),i("div",null,n)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
