import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 23.1-905 Academic credit for American Sign Language.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/23.1-905.md","filePath":"vacode/23.1-905.md"}'),t={name:"vacode/23.1-905.md"},n=r('<h1 id="§-23-1-905-academic-credit-for-american-sign-language" tabindex="-1">§ 23.1-905 Academic credit for American Sign Language. <a class="header-anchor" href="#§-23-1-905-academic-credit-for-american-sign-language" aria-label="Permalink to &quot;§ 23.1-905 Academic credit for American Sign Language.&quot;">​</a></h1><p>A. Each public institution of higher education shall develop policies for counting credit received for successful completion of foreign language courses, including American Sign Language courses, either in a secondary school or another institution of higher education toward satisfaction of the foreign language entrance, placement, and course credit requirements of the public institution of higher education that are uniform across each foreign language program offered by the institution.</p><p>B. Each public institution of higher education shall count credit received for successful completion of American Sign Language courses at the institution toward satisfaction of its foreign language course credit requirements.</p><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0762" target="_blank" rel="noreferrer">762</a>, § 23-9.2:3.9; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0292" target="_blank" rel="noreferrer">292</a>.</p>',4),o=[n];function c(s,g,l,u,d,f){return a(),i("div",null,o)}const _=e(t,[["render",c]]);export{p as __pageData,_ as default};
