import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 24.2-1012 Offenses as to absent voters.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-1012.md","filePath":"vacode/24.2-1012.md"}'),r={name:"vacode/24.2-1012.md"},n=o('<h1 id="§-24-2-1012-offenses-as-to-absent-voters" tabindex="-1">§ 24.2-1012 Offenses as to absent voters. <a class="header-anchor" href="#§-24-2-1012-offenses-as-to-absent-voters" aria-label="Permalink to &quot;§ 24.2-1012 Offenses as to absent voters.&quot;">​</a></h1><p>Any person who knowingly aids or abets or attempts to aid or abet a violation of the absentee voting procedures prescribed in § <a href="http://law.lis.virginia.gov/vacode/24.2-649/" target="_blank" rel="noreferrer">24.2-649</a> and Chapter 7 (§ <a href="http://law.lis.virginia.gov/vacode/24.2-700/" target="_blank" rel="noreferrer">24.2-700</a> et seq.) shall be guilty of a Class 5 felony.</p><p>Any person attempting to vote by fraudulently signing the name of a qualified voter shall be guilty of forgery and shall be guilty of a Class 4 felony.</p><p>Any public official who knowingly violates any of the provisions of the law concerning absent voters and thereby aids in any way the illegal casting, or attempting to cast a vote, or who connives to nullify any provisions of this chapter in order that fraud may be perpetrated, shall forever be disqualified from holding office in the Commonwealth and shall forever be disqualified from exercising the right of franchise.</p><p>Code 1950, § 24-345; 1956, c. 382; 1970, c. 462, § 24.1-274; 1993, c. 641; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0242" target="_blank" rel="noreferrer">242</a>.</p>',5),s=[n];function i(l,f,c,d,h,p){return a(),t("div",null,s)}const g=e(r,[["render",i]]);export{v as __pageData,g as default};
