import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-103.1 Duties of Department of Elections related to redistricting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-103.1.md","filePath":"vacode/24.2-103.1.md"}'),i={name:"vacode/24.2-103.1.md"},o=r('<h1 id="§-24-2-103-1-duties-of-department-of-elections-related-to-redistricting" tabindex="-1">§ 24.2-103.1 Duties of Department of Elections related to redistricting. <a class="header-anchor" href="#§-24-2-103-1-duties-of-department-of-elections-related-to-redistricting" aria-label="Permalink to &quot;§ 24.2-103.1 Duties of Department of Elections related to redistricting.&quot;">​</a></h1><p>A. Upon receipt of any ordinance and Geographic Information System (GIS) map sent pursuant to § <a href="/vacode/24.2-304.3/">24.2-304.3</a> or <a href="/vacode/24.2-306/">24.2-306</a>, the Department shall promptly review the ordinance and map and compare the boundaries contained within with the information in the voter registration system in order to ensure voters have been assigned to the correct districts. The Department shall notify the locality of any corrections that may be necessary.</p><p>B. The Department shall maintain and make available on its official website maps showing the current election district and precinct boundaries of each county and city.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0778" target="_blank" rel="noreferrer">778</a>.</p>',4),n=[o];function s(c,d,l,p,h,f){return t(),a("div",null,n)}const u=e(i,[["render",s]]);export{m as __pageData,u as default};
