import{_ as e,o,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 24.2-119.1 Prohibition on discrimination in employment; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-119.1.md","filePath":"vacode/24.2-119.1.md"}'),t={name:"vacode/24.2-119.1.md"},n=a('<h1 id="§-24-2-119-1-prohibition-on-discrimination-in-employment-penalty" tabindex="-1">§ 24.2-119.1 Prohibition on discrimination in employment; penalty. <a class="header-anchor" href="#§-24-2-119-1-prohibition-on-discrimination-in-employment-penalty" aria-label="Permalink to &quot;§ 24.2-119.1 Prohibition on discrimination in employment; penalty.&quot;">​</a></h1><p>Any person who serves as a member of a local electoral board, a deputy general registrar, or an officer of election shall neither be discharged from employment nor have any adverse personnel action taken against him, nor shall he be required to use sick leave or vacation time, as a result of his absence from employment due to his service at a polling place on election day or at a meeting of the electoral board following the election to ascertain the results of such election pursuant to § <a href="/vacode/24.2-671/">24.2-671</a>, provided that he gave reasonable notice to his employer of such service. No such person who serves for four or more hours, including travel time, on his day of service shall be required to start any work shift that begins on or after 5:00 p.m. on the day of his service or begins before 3:00 a.m. on the day following the day of his service. Any employer violating the provisions of this section shall be guilty of a Class 3 misdemeanor.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0838" target="_blank" rel="noreferrer">838</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0140" target="_blank" rel="noreferrer">140</a>.</p>',3),r=[n];function s(l,c,h,p,m,d){return o(),i("div",null,r)}const v=e(t,[["render",s]]);export{_ as __pageData,v as default};
