import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 24.2-218 Election and term of county supervisors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-218.md","filePath":"vacode/24.2-218.md"}'),a={name:"vacode/24.2-218.md"},s=r('<h1 id="§-24-2-218-election-and-term-of-county-supervisors" tabindex="-1">§ 24.2-218 Election and term of county supervisors. <a class="header-anchor" href="#§-24-2-218-election-and-term-of-county-supervisors" aria-label="Permalink to &quot;§ 24.2-218 Election and term of county supervisors.&quot;">​</a></h1><p>A. The qualified voters of each county election district shall elect one or more supervisors at the general election in November 1995, and every four years thereafter for terms of four years, except as provided in § <a href="/vacode/24.2-219/">24.2-219</a> or as provided by law for those counties having the optional form of government under the provisions of Article 2 (§ <a href="/vacode/15.2-702/">15.2-702</a> et seq.) of Chapter 7 of Title 15.2.</p><p>B. Notwithstanding any other provision of law, general or special, in a county that imposes district-based or ward-based residency requirements for members of the board of supervisors, the member elected from each district or ward shall be elected by the qualified voters of that district or ward and not by the county at large.</p><p>Code 1950, § 24-157; 1968, c. 639; 1970, c. 462, § 24.1-88; 1971, Ex. Sess., c. 265; 1973, c. 30; 1976, c. 616; 1981, c. 12; 1982, c. 650; 1993, c. 641; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0225" target="_blank" rel="noreferrer">225</a>.</p>',4),i=[s];function n(c,d,l,f,p,h){return o(),t("div",null,i)}const m=e(a,[["render",n]]);export{_ as __pageData,m as default};
