import{_ as e,o as a,c as n,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-236 Suspension from office pending hearing and appeal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-236.md","filePath":"vacode/24.2-236.md"}'),i={name:"vacode/24.2-236.md"},t=r('<h1 id="§-24-2-236-suspension-from-office-pending-hearing-and-appeal" tabindex="-1">§ 24.2-236 Suspension from office pending hearing and appeal. <a class="header-anchor" href="#§-24-2-236-suspension-from-office-pending-hearing-and-appeal" aria-label="Permalink to &quot;§ 24.2-236 Suspension from office pending hearing and appeal.&quot;">​</a></h1><p>In the event of a judicial proceeding under § <a href="http://law.lis.virginia.gov/vacode/24.2-231/" target="_blank" rel="noreferrer">24.2-231</a>, <a href="http://law.lis.virginia.gov/vacode/24.2-232/" target="_blank" rel="noreferrer">24.2-232</a>, <a href="http://law.lis.virginia.gov/vacode/24.2-233/" target="_blank" rel="noreferrer">24.2-233</a>, or <a href="http://law.lis.virginia.gov/vacode/24.2-234/" target="_blank" rel="noreferrer">24.2-234</a>, the circuit court may enter an order suspending the officer pending the hearing. Any officer convicted of a felony under the laws of any state or the United States shall be automatically suspended upon such conviction, regardless of any appeals, pleadings, delays, or motions. The court may, in its discretion, continue the suspension until the matter is finally disposed of in the Supreme Court or otherwise. During the suspension the court may appoint some suitable person to act in the officer&#39;s place. The officer&#39;s compensation shall be withheld and kept in a separate account and paid to him if and when the judicial proceedings result in his favor. Otherwise, it shall be paid back to the county, city, town, or State Treasurer who paid it.</p><p>1975, cc. 515, 595, § 24.1-79.8; 1993, c. 641; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0369" target="_blank" rel="noreferrer">369</a>.</p>',3),o=[t];function s(c,l,p,d,h,f){return a(),n("div",null,o)}const _=e(i,[["render",s]]);export{u as __pageData,_ as default};
