import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-238 Costs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-238.md","filePath":"vacode/24.2-238.md"}'),o={name:"vacode/24.2-238.md"},i=r('<h1 id="§-24-2-238-costs" tabindex="-1">§ 24.2-238 Costs. <a class="header-anchor" href="#§-24-2-238-costs" aria-label="Permalink to &quot;§ 24.2-238 Costs.&quot;">​</a></h1><p>A. If a judicial proceeding under this article is dismissed in favor of the respondent, the court in its discretion may require the state agency or political subdivision which the respondent serves to pay court costs or reasonable attorney fees, or both, for the respondent.</p><p>B. No person who signs a petition for the removal of an official pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-233/" target="_blank" rel="noreferrer">24.2-233</a> or who circulates such a petition (i) shall be liable for any costs associated with removal proceedings conducted pursuant to the petition, including attorney fees incurred by any other party or court costs, or (ii) shall have sanctions imposed against him pursuant to § <a href="http://law.lis.virginia.gov/vacode/8.01-271.1/" target="_blank" rel="noreferrer">8.01-271.1</a>.</p><p>1975, cc. 515, 595, § 24.1-79.10; 1993, c. 641; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0876" target="_blank" rel="noreferrer">876</a>.</p>',4),s=[i];function n(c,l,h,p,d,_){return t(),a("div",null,s)}const g=e(o,[["render",n]]);export{u as __pageData,g as default};
