import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 24.2-304.3 Recording reapportionment ordinance; notice requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-304.3.md","filePath":"vacode/24.2-304.3.md"}'),n={name:"vacode/24.2-304.3.md"},i=r('<h1 id="§-24-2-304-3-recording-reapportionment-ordinance-notice-requirements" tabindex="-1">§ 24.2-304.3 Recording reapportionment ordinance; notice requirements. <a class="header-anchor" href="#§-24-2-304-3-recording-reapportionment-ordinance-notice-requirements" aria-label="Permalink to &quot;§ 24.2-304.3 Recording reapportionment ordinance; notice requirements.&quot;">​</a></h1><p>A copy of the ordinance reapportioning representation in the governing body of a county, city, or town, including a description of the boundaries and a map showing the boundaries of the districts or wards, shall be recorded in the official minutes of the governing body.</p><p>The clerk of the county, city, or town shall send a certified copy of the ordinance, including a description of the boundaries and a Geographic Information System (GIS) map showing the boundaries of the districts or wards, to the local electoral board, the Secretary of the Commonwealth, the Department of Elections, and the Division of Legislative Services. Any county, city, or town that does not have GIS capabilities may request the Department of Elections to create on its behalf a GIS map showing the boundaries of the districts or wards as set out in the ordinance, and the Department of Elections shall create such a map.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0249" target="_blank" rel="noreferrer">249</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0778" target="_blank" rel="noreferrer">778</a>.</p>',4),a=[i];function c(s,d,h,l,p,f){return t(),o("div",null,a)}const _=e(n,[["render",c]]);export{m as __pageData,_ as default};
