import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-404.4 Exchange of registered voter lists with other states.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-404.4.md","filePath":"vacode/24.2-404.4.md"}'),i={name:"vacode/24.2-404.4.md"},o=a('<h1 id="§-24-2-404-4-exchange-of-registered-voter-lists-with-other-states" tabindex="-1">§ 24.2-404.4 Exchange of registered voter lists with other states. <a class="header-anchor" href="#§-24-2-404-4-exchange-of-registered-voter-lists-with-other-states" aria-label="Permalink to &quot;§ 24.2-404.4 Exchange of registered voter lists with other states.&quot;">​</a></h1><p>A. Pursuant to its authority under subsection A of § <a href="/vacode/24.2-405/">24.2-405</a> and subsections B and C of § <a href="/vacode/24.2-406/">24.2-406</a>, the Department of Elections shall request voter registration information and lists of persons voting at primaries and elections, if available, from the states bordering the Commonwealth to identify duplicate registrations, voters who no longer reside in the Commonwealth, and other persons who are no longer entitled to be registered in order to maintain the overall accuracy of the voter registration system.</p><p>B. Pursuant to its authority under subdivision A 10 of § <a href="/vacode/24.2-404/">24.2-404</a>, the Department of Elections shall utilize data regarding voter registration and lists of persons voting at primaries and elections received through list comparisons with other states to identify duplicate registrations, voters who no longer reside in the Commonwealth, and other persons who are no longer entitled to be registered in order to maintain the overall accuracy of the voter registration system.</p><p>C. The Department shall compare the data received pursuant to subsections A and B with the state voter registration list and initiate list maintenance procedures under applicable state and federal law. The Department shall include in its report to the House and Senate Committees on Privileges and Elections, required by subsection F of § <a href="/vacode/24.2-404/">24.2-404</a>, the progress of activities conducted under this section, including the number of duplicate registrations found to exist and the procedures that the Department and general registrars are following to eliminate duplicate registrations from the Virginia registered voter lists.</p><p>2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0435" target="_blank" rel="noreferrer">435</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0713" target="_blank" rel="noreferrer">713</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0460" target="_blank" rel="noreferrer">460</a>.</p>',5),s=[o];function n(l,h,d,c,g,f){return t(),r("div",null,s)}const v=e(i,[["render",n]]);export{u as __pageData,v as default};
