import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 24.2-416.4 Return of mail voter registration applications.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-416.4.md","filePath":"vacode/24.2-416.4.md"}'),i={name:"vacode/24.2-416.4.md"},o=r('<h1 id="§-24-2-416-4-return-of-mail-voter-registration-applications" tabindex="-1">§ 24.2-416.4 Return of mail voter registration applications. <a class="header-anchor" href="#§-24-2-416-4-return-of-mail-voter-registration-applications" aria-label="Permalink to &quot;§ 24.2-416.4 Return of mail voter registration applications.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/24.2-416/" target="_blank" rel="noreferrer">24.2-416</a>, a mail voter registration application returned through the United States Postal Service shall be deemed to have been made as of the date of the postmark affixed to such application by the United States Postal Service. If no such postmark is affixed or if the postmark affixed by the United States Postal Service is illegible or bears no date, such application shall be deemed to have been timely if received through the United States mail no later than five days following the time for the closing of the registration books pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-416/" target="_blank" rel="noreferrer">24.2-416</a>.</p><p>B. In any other case, a completed mail voter registration application shall be deemed timely if received by any general registrar or any person authorized to receive voter registration applications pursuant to § <a href="http://law.lis.virginia.gov/vacode/24.2-415.1/" target="_blank" rel="noreferrer">24.2-415.1</a> by 5:00 p.m. on the final day of registration provided for in § <a href="http://law.lis.virginia.gov/vacode/24.2-414/" target="_blank" rel="noreferrer">24.2-414</a>.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0072" target="_blank" rel="noreferrer">72</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0073" target="_blank" rel="noreferrer">73</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0680" target="_blank" rel="noreferrer">680</a>.</p>',4),n=[o];function l(s,p,c,h,f,d){return t(),a("div",null,n)}const _=e(i,[["render",l]]);export{v as __pageData,_ as default};
