import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 24.2-417.1 Registration residence requirements; presumptions in certain cases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-417.1.md","filePath":"vacode/24.2-417.1.md"}'),i={name:"vacode/24.2-417.1.md"},n=r('<h1 id="§-24-2-417-1-registration-residence-requirements-presumptions-in-certain-cases" tabindex="-1">§ 24.2-417.1 Registration residence requirements; presumptions in certain cases. <a class="header-anchor" href="#§-24-2-417-1-registration-residence-requirements-presumptions-in-certain-cases" aria-label="Permalink to &quot;§ 24.2-417.1 Registration residence requirements; presumptions in certain cases.&quot;">​</a></h1><p>In determining the residence as defined in § <a href="http://law.lis.virginia.gov/vacode/24.2-101/" target="_blank" rel="noreferrer">24.2-101</a> and the domicile and place of abode of a participant in the American Conservation and Youth Service Corps provided for by federal law (42 U.S.C. § 12655 et seq.), there shall be a presumption that a participant in the Corps who was domiciled and had a place of abode in Virginia at the time of entering the Corps continues to be domiciled and retains the same place of abode unless the participant expressly states otherwise.</p><p>In determining the residence as defined in § <a href="http://law.lis.virginia.gov/vacode/24.2-101/" target="_blank" rel="noreferrer">24.2-101</a> and domicile and place of abode of a military or merchant marine spouse or dependent, there shall be a presumption that a military or merchant marine spouse or dependent who has established physical presence and a place of abode in the Commonwealth shall also have established domicile in the Commonwealth unless the spouse or dependent expressly states otherwise. Once residence is changed, the military or merchant marine spouse or dependent may not revert to any previous residence without re-establishing new physical presence and intent to remain or return.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0231" target="_blank" rel="noreferrer">231</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0391" target="_blank" rel="noreferrer">391</a>.</p>',4),s=[n];function o(c,d,p,l,h,m){return a(),t("div",null,s)}const f=e(i,[["render",o]]);export{u as __pageData,f as default};
