import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 24.2-444 Duties of general registrars and Department of Elections as to voter registration records; public inspection; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/24.2-444.md","filePath":"vacode/24.2-444.md"}'),a={name:"vacode/24.2-444.md"},n=i('<h1 id="§-24-2-444-duties-of-general-registrars-and-department-of-elections-as-to-voter-registration-records-public-inspection-exceptions" tabindex="-1">§ 24.2-444 Duties of general registrars and Department of Elections as to voter registration records; public inspection; exceptions. <a class="header-anchor" href="#§-24-2-444-duties-of-general-registrars-and-department-of-elections-as-to-voter-registration-records-public-inspection-exceptions" aria-label="Permalink to &quot;§ 24.2-444 Duties of general registrars and Department of Elections as to voter registration records; public inspection; exceptions.&quot;">​</a></h1><p>A. Registration records shall be kept and preserved by the general registrar in compliance with §§ <a href="http://law.lis.virginia.gov/vacode/2.2-3803/" target="_blank" rel="noreferrer">2.2-3803</a>, <a href="http://law.lis.virginia.gov/vacode/2.2-3808/" target="_blank" rel="noreferrer">2.2-3808</a>, and <a href="http://law.lis.virginia.gov/vacode/24.2-114/" target="_blank" rel="noreferrer">24.2-114</a>. The Department shall provide to each general registrar, for each precinct in his county or city, lists of registered voters for inspection. The lists shall contain the name, address, year of birth, gender and all election districts applicable to each registered voter. The lists shall be opened to public inspection at the office of the general registrar when the office is open for business. New lists shall be provided not less than once each year to all localities except those in which an updated list is made available electronically for public inspection, and supplements containing additions, deletions, and changes shall be provided not less than (i) weekly during the 60 days preceding any general election and (ii) monthly at other times. Notwithstanding any other provision of law regarding the retention of records, upon receipt of any new complete list, the general registrar shall destroy the obsolete list and its supplements. The Department shall provide to each general registrar lists of persons denied registration for public inspection. Such lists may be provided electronically through the Virginia voter registration system and produced in whole or in part upon a request for public inspection.</p><p>B. The general registrars shall maintain for at least two years and shall make available for public inspection and copying and, where available, photocopying at a reasonable cost, all records concerning the implementation of programs and activities conducted for the purpose of ensuring the accuracy and currency of the registration records pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/24.2-427/" target="_blank" rel="noreferrer">24.2-427</a>, <a href="http://law.lis.virginia.gov/vacode/24.2-428/" target="_blank" rel="noreferrer">24.2-428</a> and <a href="http://law.lis.virginia.gov/vacode/24.2-428.1/" target="_blank" rel="noreferrer">24.2-428.1</a>, including lists of the names and addresses of all persons to whom notices are sent, and information concerning whether each person has responded to the notice as of the date that inspection of the records is made.</p><p>C. No list provided by the Department under subsection A nor any record made available for public inspection under subsection B shall contain any of the following information: (i) an individual&#39;s social security number, or any part thereof; (ii) the residence address of an individual who has furnished a post office box address in lieu of his residence address as authorized by subsection B of § <a href="http://law.lis.virginia.gov/vacode/24.2-418/" target="_blank" rel="noreferrer">24.2-418</a>; (iii) the declination by an individual to register to vote and related records; (iv) the identity of a voter registration agency through which a particular voter is registered; or (v) the day and month of birth of an individual. No voter registration records other than the lists provided by the Department under subsection A and the records made available under subsection B shall be open to public inspection.</p><p>Code 1950, § 24-113; 1970, c. 462, § 24.1-56; 1993, c. 641; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0656" target="_blank" rel="noreferrer">656</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0072" target="_blank" rel="noreferrer">72</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0073" target="_blank" rel="noreferrer">73</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0612" target="_blank" rel="noreferrer">612</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0626" target="_blank" rel="noreferrer">626</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1015" target="_blank" rel="noreferrer">1015</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0311" target="_blank" rel="noreferrer">311</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0318" target="_blank" rel="noreferrer">318</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0379" target="_blank" rel="noreferrer">379</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0318" target="_blank" rel="noreferrer">318</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0353" target="_blank" rel="noreferrer">353</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0740" target="_blank" rel="noreferrer">740</a>.</p>',5),o=[n];function s(l,c,d,g,h,p){return r(),t("div",null,o)}const u=e(a,[["render",s]]);export{v as __pageData,u as default};
